body.div:
  {% for symbol in symbols %}






{{ symbol.name }}

  {% endfor %}

body.script:
const symbols = [
  { id: 'glyph10', name: 'Number 10' },
  { id: 'glyph32', name: 'Space' },
  { id: 'glyph33', name: 'Exclamation mark' },
  { id: 'glyph46', name: '.' },
  { id: 'glyph72', name: 'R' },
  { id: 'glyph84', name: 'T' },
  { id: 'glyph87', name: 'W' },
  { id: 'glyph97', name: 'a' },
  { id: 'glyph99', name: 'c' },
  { id: 'glyph100', name: 'd' },
  { id: 'glyph101', name: 'e' },
  { id: 'glyph104', name: 'i' },
  { id: 'glyph107', name: 'j' },
  { id: 'glyph108', name: 'l' },
  { id: 'glyph109', name: 'm' },
];

const svgContainer = document.querySelector('.svg-container');

symbols.forEach(symbol => {
  const svgElement = document.createElementNS('http://www.w3.org/2000/svg', 'svg');
  svgElement.setAttribute('viewBox', `0 0 ${symbol.width} ${symbol.height}`);
  svgElement.innerHTML = ``;

  const imgElement = document.createElement('img');
  imgElement.src = window.URL.createObjectURL(svgElement);

  const svgItem = document.createElement('div');
  svgItem.classList.add('svg-item');
  svgItem.appendChild(imgElement);
  svgItem.appendChild(document.createTextNode(` ${symbol.name}`));

  svgContainer.appendChild(svgItem);
});
